package automation;

public class NumberSwapWithoutVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a= 10;
		System.out.println("Value of a before swapping"+ a);
		
		int b= 20;
		
		a=b;
		
		System.out.println("Value of a after swapping"+ a);
		

	}

}
