package automation;

public class StringAssignment5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str= "My name is Deepak";
		char ch ='e';
		
		int Counter=0;
		
		for(int i=0; i<str.length();i++)
		{
			if(ch==str.charAt(i))
			{
				++Counter;
			}
			}
		    System.out.println("Character e appear"+ Counter +" "+"times");
				
		}
		
		
		}


		
	

		
		

	


